mapper.select:
SELECT distinct t.* FROM uc_message_thread t
INNER JOIN uc_message_thread_members tm ON t.id = tm.thread_id AND t.deleted = FALSE AND tm.deleted = FALSE
and t.type=#{type}
and tm.member_id in

	#{item}

order by t.last_message_id desc

SELECT distinct t.* FROM uc_message_thread t
INNER JOIN uc_message_thread_members tm ON t.id = tm.thread_id AND t.deleted = FALSE AND tm.deleted = FALSE and t.type=1 
and tm.member_id = #{memberId}
order by t.last_message_id desc
limit #{start},#{end}

SELECT distinct t.* FROM uc_message_thread t
INNER JOIN uc_message_thread_members tm ON t.id = tm.thread_id AND t.deleted = FALSE AND tm.deleted = FALSE and t.type=2  and tm.member_id = #{memberId}
inner join uc_message m on m.thread_id = t.id and m.deleted=false

	 and m.type =  3 	and m.member_id != #{memberId}


	 and m.type=3 and m.member_id=#{memberId} 

order by t.last_message_id desc
limit #{start},#{end}

SELECT count(distinct t.id) FROM uc_message_thread t INNER JOIN uc_message_thread_members tm ON t.id = tm.thread_id 
AND t.deleted = FALSE AND tm.deleted = FALSE and t.type=2 and tm.member_id = #{memberId}
inner join uc_message m on m.thread_id = t.id and m.deleted=false

	 and m.type =  3 and m.member_id != #{memberId}


	 and m.type=3 and m.member_id=#{memberId}

	SELECT distinct t.* FROM uc_message_thread t
	INNER JOIN uc_message_thread_members tm ON t.id = tm.thread_id AND t.deleted = FALSE AND tm.deleted = FALSE and t.type=2  and t.owner_id = #{memberId}

 		inner join uc_message m on m.thread_id = t.id and m.deleted=false
 		and ( t.is_public = true or ( m.member_id= #{currentUserId}) )

 	order by t.last_message_id desc
	limit #{start},#{end}

SELECT t.thread_id,count(1) as c_count FROM uc_message t INNER JOIN uc_message_thread_members um ON t.thread_id = um.thread_id and t.id > um.last_position and t.member_id != #{memberId} and um.member_id=#{memberId}
and um.thread_id in
#{item}
group by t.thread_id
